const {id} = useParams();
if (id) {
    const data = getProductsByCategory(id);
}
const llamadaDatos = id ? getProductsByCategory : getAllProducts;


    return (
        <div className="container">
            <h1>{greeting}</h1>
                <div className='row justify-content-center'>
                    <ItemList products = { data }/>
                </div>            
            
            
        </div>
            
        
    );
}
