import { useState } from 'react'
import Button from 'react-bootstrap/Button';
export const ItemCount = ({stock, inicial, onAdd}) => {
    const [cantidad, setCantidad] = useState(inicial);

    const sumar = () => {
        if (cantidad < stock) {
            setCantidad(cantidad+1);
        }
    }

    const restar = () => {
        if (cantidad > 1) {
            setCantidad(cantidad-1);
        }
    }
return(
    <div className='flex'>
        <div>
        <button className='' onClick={ restar }>-</button>
        <h4>{cantidad}</h4>
        <button className='' onClick={ sumar }>+</button>
        </div>
        <div>
            <Button variant='info' onClick={() => onAdd(cantidad)} disabled={!stock}>Agregar</Button>
        </div>
    </div>
);
}
