import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './Item.css';
const Item = ({ product }) => {
    return (
        <Card style={{width: '400px'}}>
            <Card.Img variant='top' src={product.image} style={{width: 'auto', height: '300px'}}/>
            <Card.Body>
                <Card.Title>{product.title}</Card.Title>
                <Card.Text>
                    {product.description}
                </Card.Text>
                <Card.Text>
                    $ {product.price}
                </Card.Text>
                <Button variant="primary">Agregar al Carrito</Button>
            </Card.Body>
        </Card>
    );
}
export default Item;
