import { useState } from 'react'
export const ItemCount = ({stock, inicial, agregar}) => {
    const [cantidad, setCantidad] = useState(inicial);

    const sumar = () => {
        if (cantidad < stock) {
            setCantidad(cantidad++);
        }
    }

    const restar = () => {
        if (cantidad > 1) {
            setCantidad(cantidad--);
        }
    }
return(
    <div className='flex'>
        <button className='' onClick={ restar }>-</button>
        <h4>{cantidad}</h4>
        <button className='' onClic={ sumar }>+</button>
    </div>
);
}
