import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { ItemCount } from '../ItemCount/ItemCount';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const enviarNotificacion = (cantidad) => {
    toast.success('Agregaste ' + cantidad + ' unidades al Carrito');
}

const Item = ({ id, title, description, image, price }) => {
    return (
        <div className="col-sm-8 col-md-6 col-lg-4 col-xl-3 mt-1">
        <Card> 
            <Card.Img variant='top' src={image} style={{height: '300px'}}/>
            <Card.Body>
                <Card.Title>{title}</Card.Title>
                <Card.Text>
                    {description}
                </Card.Text>
                <Card.Text>
                    $ {price}
                </Card.Text>
                <Button variant="primary">Agregar al Carrito</Button>
                <ItemCount stock={100} inicial={1} onAdd={(cantidad) => enviarNotificacion(cantidad)}/>
                <ToastContainer
                    hideProgressBar= {true}
                    autoClose={1000}
                    theme='colored'
                    pauseOnHover={false}
                    closeOnClick
                />
            </Card.Body>
        </Card>
        </div>
    );
}
export default Item;
