import { useState } from 'react'
export const ItemCount = ({stock, inicial, onAdd}) => {
    let [cantidad, setCantidad] = useState(inicial);

    const sumar = () => {
        if (cantidad < stock) {
            setCantidad(cantidad++);
        }
    }

    const restar = () => {
        if (cantidad > 1) {
            setCantidad(cantidad-1);
        }
    }
return(
    <div className='flex'>
        <div>
        <button className='' onClick={ restar }>-</button>
        <h4>{cantidad}</h4>
        <button className='' onClick={ sumar }>+</button>
        </div>
        <div>
            <button onClick={() => onAdd(cantidad)} disabled={!stock}>Agregar</button>
        </div>
    </div>
);
}
